export default function MissionEnvelope({ isOpen, setIsOpen, onOpened, mission }) {
  const handleClick = () => {
    if (isOpen) return; // open only once
    setIsOpen(true);

    // wait a bit so envelope opens, then show modal
    setTimeout(() => {
      onOpened?.();
    }, 600);
  };


  return (
    <button onClick={handleClick} className="relative mt-10 focus:outline-none">
      <div
        className={`
          transition-transform duration-900 ease-out
         
        `}
      >
        {isOpen ? <OpenEnvelopeSvg /> : <ClosedEnvelopeSvg />}
      </div>

      <p className="mt-6 text-base tracking-[0.1em] text-[#83593E] text-center font-cinzel">
        {mission?.title}
      </p>
    </button>
  );
}
